defaults.rulesCode:
if (params.action == "create") {

    return Array.from({ length: params.rows }, () =>
        Array.from({ length: params.columns }, () => ({ value: "false" }))
    )

} else if (params.action == "update") {
    return params.grid
}

defaults.html:
//@card/react

function Widget(card) {
  if (card?.value?.length > 0) {
    return <InteractiveGrid
      onChange={(data) => execute_action(card.name, { grid: data })}
      data={card.value}
    />
  } else {
    return <Tinted>
      <ProtoThemeProvider forcedTheme={window.TamaguiTheme}>
        <ActionCard data={card}>
          <ParamsForm data={card}>
            <YStack flex={1} jc="center" ai="center" gap="$5">
            {card.icon && card.displayIcon !== false && (
          <Icon name={card.icon} size={48} color={card.color}/>
      )}
              <Text maw="320px" textAlign="center">Add the number of rows and columns you want and click "Create" to create a grid</Text>
            </YStack>
          </ParamsForm>
        </ActionCard>
      </ProtoThemeProvider>
    </Tinted>
  }
}
