defaults.rulesCode:
if (userParams.editedValue) {
  return userParams.editedValue;
} else {
  return userParams.state;
}

defaults.html:
//@card/react

function Widget(card) {
  const value = card.value;

  const content = <YStack f={1} mt={"20px"} ai="center" jc="center" width="100%">
    {card.icon && card.displayIcon !== false && (
      <Icon name={card.icon} size={48} color={card.color} />
    )}
    {card.displayResponse !== false && (
      <CardValue
        mode={card.markdownDisplay? "markdown" : card.htmlDisplay ? 'html' : 'normal'}
        value={value}
        readOnly={false}
        executeActionOnEdit={(val) => { executeAction(card.name, { editedValue: val }) }}
      />
    )}
  </YStack>

  return (
    <Tinted>
      <ProtoThemeProvider forcedTheme={window.TamaguiTheme}>
        <ActionCard data={card}>
          {card.displayButton !== false ? <ParamsForm data={card}>{content}</ParamsForm> : card.displayResponse !== false && content}
        </ActionCard>
      </ProtoThemeProvider>
    </Tinted>
  );
}
