//@card/react

function Widget(card) {
  const value = card.value;

  return (
      <Tinted>
        <ProtoThemeProvider forcedTheme={window.TamaguiTheme}>
          <ActionCard data={card}>
            <img style={{width: "100%", height: "100%", objectFit: "contain"}} src={value}/>
          </ActionCard>
        </ProtoThemeProvider>
      </Tinted>
  );
}
