reactCard(`
  function Widget(props) {
    console.log('react object widget: ', props.value)
    return (
      <Tinted>
        <ViewObject
          object={props.value}
          onAdd={(key, value) => execute_action('${data.name}', { action: 'set', key, value })}
          onValueEdit={(key, value) => execute_action('${data.name}', { action: 'set', key, value })}
          onKeyDelete={(key) => execute_action('${data.name}', { action: 'delete', key })}
          onKeyEdit={(oldKey, newKey) => execute_action('${data.name}', { action: 'rename', oldKey, newKey })}
          onClear={() => execute_action('${data.name}', { action: 'reset' })}
        />
      </Tinted>
    );
  }
`, data.domId, data)

defaults.rulesCode:
if (params.action === 'reset' || params.action === 'clear') {
  return {};
} else if (params.action === 'set') {
  const key = params.key
  const value = params.value
  return { ...(board?.[name] ?? {}), [key]: value }
} else if (params.action === 'delete') {
  const newObj = { ...(board?.[name] ?? {}) }
  delete newObj[params.key]
  return newObj
} else if (params.action === 'rename') {
  const oldKey = params.oldKey
  const newKey = params.newKey
  const obj = { ...(board?.[name] ?? {}) }
  if (oldKey !== newKey && obj[oldKey] !== undefined && obj[newKey] === undefined) {
    obj[newKey] = obj[oldKey]
    delete obj[oldKey]
  }
  return obj
} else {
  return board?.[name] ?? {}
}
