//@card/react

function Widget(card) {
  const value = card.value;
  async function validateKey(apiKey) { return true; }
  const nameKey = 'OPENAI_API_KEY';

  const readme = `
### üîë How to get your OpenAI API key?
1. Go to [OpenAI's API Keys page](https://platform.openai.com/account/api-keys).
2. Log in and click **"Create new secret key"**.
3. Copy and save your key securely, it won't be shown again.
---
> ‚ö†Ô∏è **Keep it secret!** Your API key is private and usage-based.
`;

  const content = (
    <YStack f={1} mt={"20px"} ai="center" jc="center" width="100%">
      {card.icon && card.displayIcon !== false && (
        <Icon name={card.icon} size={48} color={card.color} />
      )}
      {card.displayResponse !== false && (
        <CardValue mode={card.markdownDisplay ? 'markdown' : 'normal'} value={value ?? "N/A"} readOnly={true}/>
      )}
    </YStack>
  );

  return (
    <Tinted>
      <ProtoThemeProvider forcedTheme={window.TamaguiTheme}>
        <KeyGate requiredKeys={[nameKey]} readme={readme} >
          <ActionCard data={card}>
            {card.displayButton !== false
              ? <ParamsForm data={card}>{content}</ParamsForm>
              : card.displayResponse !== false && content}
          </ActionCard>
        </KeyGate>
      </ProtoThemeProvider>
    </Tinted>
  );
}
