//@card/react
function Widget(props) {
    return (
        <Tinted>
            <CameraCard params={props.configParams} onPicture={async (picture64) => {
              const url = document.location.origin + (await execute_action('/api/core/v1/vision/frame/set', {id: 'frame', image: picture64}))
              execute_action(props.name, {picture: url})
            }}/>
        </Tinted>
    );
  }

